import React from "react";
import { Link } from "react-router-dom";

const Header = () => {
  return (
    <header className="bg-blue-600 text-white shadow-lg">
      <div className="container mx-auto px-4 py-4">
        <div className="flex items-center justify-between">
          <Link to="/" className="flex items-center space-x-3">
            <div className="w-10 h-10 bg-white rounded-lg flex items-center justify-center">
              <span className="text-blue-600 font-bold text-xl">C</span>
            </div>
            <h1 className="text-2xl font-bold">CivicIssueReporter</h1>
          </Link>
          
          <nav className="hidden md:flex space-x-6">
            <Link to="/" className="hover:text-blue-200 transition-colors">Home</Link>
            <Link to="/admin" className="hover:text-blue-200 transition-colors">Admin</Link>
            <Link to="/profile" className="hover:text-blue-200 transition-colors">Profile</Link> {/* Add this */}
          </nav>
          
          <div className="flex space-x-4">
            <Link to="/login" className="bg-white text-blue-600 px-4 py-2 rounded-lg font-medium hover:bg-blue-100 transition-colors">
              Login
            </Link>
            <Link to="/register" className="bg-blue-700 text-white px-4 py-2 rounded-lg font-medium hover:bg-blue-800 transition-colors">
              Register
            </Link>
          </div>
        </div>
      </div>
    </header>
  );
};

export default Header;
